import React, { Component } from 'react';
import Inputs from '../components/input';

class form extends Component {

    constructor(){
        super();
        this.state = {
            tasks:[],
        };
    }

    saveTask = (task) => {

        this.setState({
            tasks:[
                ...this.state.tasks,
                task
            ]
        });

    }

    render() {
        return (
            <div className="container">
                <div className="row justify-content-center">
                    <div className="col-6 ">
                    <form>
                        <Inputs saveTask={this.saveTask} />
                    </form>
                    </div>
                </div>
            </div>
        );
    }
}

export default form;
