import React, { Component } from 'react';

class RowTask extends Component {

    constructor(){
        super();

        this.state = {
            delTask:false
        }
    }

    handleEventDelTask = (e) => {
        this.setState({
            delTask:true
        });

        console.log(this.state.delTask);
    }

    render() {    
        const { task } = this.props;

        return (
            <tr>

                <td className="text-center">1</td>
                <td className="text-center">{task.task}</td>
                <td className="text-center">{task.date}</td>
                <td className="text-center">{task.time}</td>
                <td className="text-center"> <input type="checkbox" onChange={this.handleEventDelTask} /> </td>
                <td className="text-center"><button className="btn btn-danger">Eliminar</button></td>

            </tr>
        );
    }
}

export default RowTask;
