import React, { Component } from 'react';

class input extends Component {

    constructor(){
        super();
        this.state = {
            task:{
                task: null,
                date: null,
                time: null
            },
            error:false,
            success:false,
        };
    }

    handleInput = event => {
        this.setState({
            task:{
                ...this.state.task,
                [event.target.name] : [event.target.value],
            }
        });
    };


    handleSubmit = () => {
        const { saveTask } = this.props;
        const { task, date, time } = this.state.task;

        if(task === null || date === null || time === null) {

            this.setState({
                error:true,
            });

        } else {
            saveTask(this.state.task);
            this.setState({
                task:{
                    task: null,
                    date: null,
                    time: null
                },
                error: false,
                success: true,
            });
        }

    };

    render() {
        const { error, success } = this.state;
        const { task, date, time } = this.state.task;

        return (
            <div>
                {error ? 
                    <div className="alert alert-danger">Todos los campos son obligatorios</div>
                : 
                    null
                }

                {success ? 
                    <div className="alert alert-success">Tu tarea ha sido creada.</div>
                : 
                    null
                }

                <div className="form-group">
                    <label>¿Qué quieres hacer?</label>
                    <input type="email" className="form-control" name="task" value={task} onChange={this.handleInput}/>
                </div>
                <div className="form-group">
                    <label>¿Cuando?</label>
                    <input type="date" className="form-control" name="date" onChange={this.handleInput} />
                </div>
                <div className="form-group">
                    <label>¿A que hora?</label>
                    <input type="time" className="form-control" name="time" onChange={this.handleInput} />
                </div>
                <button type="button" onClick={this.handleSubmit} className="btn btn-primary">Crear Tarea</button>
            </div>
        );
    }
}

export default input;
