import React, { Component } from 'react';

class input extends Component {

    constructor(){
        super();
        this.state = {
            task:{
                task: null,
                date: null,
                time: null
            }
        };
    }

    handleSubmit = () => {};

    handleInput = event => {
        this.setState({
            ...this.state,

        });
    };

    render() {
        return (
            <div>
                <div className="form-group">
                    <label>¿Qué quieres hacer?</label>
                    <input type="email" className="form-control" name="task" onChange={this.handleInput}/>
                </div>
                <div className="form-group">
                    <label>¿Cuando?</label>
                    <input type="date" className="form-control" name="date" onChange={this.handleInput} />
                </div>
                <div className="form-group">
                    <label>¿A que hora?</label>
                    <input type="time" className="form-control" name="time" onChange={this.handleInput} />
                </div>
                <button type="submit" onClick={this.handleSubmit} className="btn btn-primary">Crear Tarea</button>
            </div>
        );
    }
}

export default input;
